package org.canzuo.spring.rocketmq.action;import org.springframework.stereotype.Component;/** * @author empiregeneral */@Componentpublic class DrawSierpinskiFractal {    private static void recurse(int n, char[][] out, int x, int y) {        if (n == 1) {            out[x][y] = out[x - 1][y + 1] = '/';            out[x][y + 1] = out[x][y + 2] = '_';            out[x][y + 3] = out[x - 1][y + 2] = '\\';            return;        }        int ofs = 1 << (n - 1);        recurse(n - 1, out, x, y);        recurse(n - 1, out, x, y + 2 * ofs);        recurse(n - 1, out, x - ofs, y + ofs);    }    public void action(int input) throws Exception {            int n = input;            if (n == 0) {                return;            }            int xlen = 1 << n;            int ylen =  1 << (n+1);            char[][] out = new char[xlen][ylen];            for (int i = 0; i < xlen; i++) {                for (int j = 0; j < ylen; j++) {                    out[i][j] = ' ';                }            }            recurse(n, out, xlen - 1, 0);            for (int i = 0; i < xlen; i++) {                for (int j = ylen - 1; j >= 0; j--) {                    if (out[i][j] != ' ') {                        System.out.println(new String(out[i], 0, j + 1));                        break;                    }                }            }            System.out.println();        }    }