package org.canzuo.spring.rocketmq.consumer;import org.apache.rocketmq.spring.annotation.RocketMQMessageListener;import org.apache.rocketmq.spring.core.RocketMQListener;import org.canzuo.spring.rocketmq.publish.DrawSierpinskiEvenPublish;import org.springframework.stereotype.Service;import javax.annotation.Resource;/** * @author empiregeneral */@Service@RocketMQMessageListener(topic = "${rocketmq.topic}", consumerGroup = "${rocketmq.producer.group}")public class StringConsumer implements RocketMQListener<String> {    @Resource    DrawSierpinskiEvenPublish publish;    @Override    public void onMessage(String message) {        try {            publish.publishEvent(Integer.parseInt(message));        } catch (Exception e) {            e.printStackTrace();        }    }}