package org.canzuo.spring.rocketmq.publish;import org.canzuo.spring.rocketmq.event.DrawSierpinskiEvent;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.ApplicationContext;import org.springframework.stereotype.Component;/** * @author empiregeneral * @Description 发布类，发布Draw SierpinskiFractal事件 */@Componentpublic class DrawSierpinskiEvenPublish {    @Autowired    private ApplicationContext context;    public void publishEvent(int input) {        DrawSierpinskiEvent event = new DrawSierpinskiEvent(input);        context.publishEvent(event);    }}